mapper.select:
		select  
		from gdsap_charge_transaction_record 
		
			
				and USER_NAME = #{userName,jdbcType=VARCHAR} 
			
			
				and INSERT_TIME >= #{startInsertTime,jdbcType=TIMESTAMP} and
				INSERT_TIME <= #{endInsertTime,jdbcType=TIMESTAMP}  
			
			
				and INSERT_TIME >= #{startInsertTime,jdbcType=TIMESTAMP} 
			
			
				and INSERT_TIME <= #{endInsertTime,jdbcType=TIMESTAMP}
			
			
				and TRANSACTION_NUM = #{transactionNum,jdbcType=DOUBLE} 
      		
			
				and TRANSACTION_TYPE = #{transactionType,jdbcType=INTEGER} 
      		
      		
				and RECORD_TYPE = #{recordType,jdbcType=INTEGER} 
      		
      		
				and PURPOSE = #{purpose,jdbcType=INTEGER} 
      		
		 
		order by INSERT_TIME DESC

		select count(0) as a from gdsap_charge_transaction_record 
		
			
				and USER_NAME = #{userName,jdbcType=VARCHAR} 
			
			
				and INSERT_TIME >= #{startInsertTime,jdbcType=TIMESTAMP} and
				INSERT_TIME <= #{endInsertTime,jdbcType=TIMESTAMP}  
			
			
				and INSERT_TIME >= #{startInsertTime,jdbcType=TIMESTAMP} 
			
			
				and INSERT_TIME <= #{endInsertTime,jdbcType=TIMESTAMP}
			
			
				and TRANSACTION_NUM = #{transactionNum,jdbcType=DOUBLE} 
      		
			
				and TRANSACTION_TYPE = #{transactionType,jdbcType=INTEGER} 
      		
      		
				and RECORD_TYPE = #{recordType,jdbcType=INTEGER} 
      		
      		
				and PURPOSE = #{purpose,jdbcType=INTEGER}
